"use strict";(()=>{$("[data-post-wrapper]").each(function(){let o=$(this).find("[data-post-link]").attr("href"),t=$(this).find("[data-read-time]");$.ajax({url:o,success:function(l){let c=$(l).find('[count-me="true"]'),n=250,i=10,e=0,s=0;c.each(function(){e+=$(this).text().split(" ").length,s+=$(this).find("img").length});let a=Math.floor(e/n+s*i/60);e/n*60+s*i<60?t.text("Less than 1 minute"):a===1?t.text("1 minute"):t.text(`${a} minutes`)}})});})();
